mapper.update:
UPDATE hotel
SET havg = #{avg}
WHERE hnum=#{company}

UPDATE air
SET airavg = #{avg}
WHERE anum=#{company}

UPDATE rentCompany
SET rentavg = #{avg}
WHERE rnum=#{company}

UPDATE Leisure
SET lavg = #{avg}
WHERE lnum=#{company}

mapper.select:
SELECT * FROM evaluate
WHERE category=#{category} AND
      company=#{company}
LIMIT #{pageNum}, #{listCnt}

SELECT score FROM evaluate
WHERE category=#{category} AND
    company=#{company}

SELECT count(*) FROM evaluate
    WHERE category=#{category} AND
    company=#{company}
